var ws;
var locationsOnTheMap = {};
var map;
var QltMin = 111;
var QltMax = 120000;
var qlTimeout = QltMin;
var ncaptures;
var qtime;
var ptimer;
var msgq;

function processMsg() {
  var msg = msgq.shift();
  if ( msg !== undefined ) {
    writeToMap(msg);
  }
  ptimer = setTimeout(processMsg, qlTimeout);
}

function initialize() {
  var mapOptions = {
    zoom: 3,
    center: new google.maps.LatLng(37.09024, 0),
    mapTypeId: google.maps.MapTypeId.ROADMAP,
    backgroundColor: '#0e3d4e',
    styles: [{
        featureType: "all",
        elementType: "all",
        stylers: [{
            visibility: "on"
        }, {
            hue: "#131c1c"
        }, {
            saturation: "-50"
        }, {
            invert_lightness: true
        }]
    }, {
        featureType: "water",
        elementType: "all",
        stylers: [{
            visibility: "on"
        }, {
            hue: "#005eff"
        }, {
            invert_lightness: true
        }]
    }, {
        featureType: "poi",
        stylers: [{
            visibility: "off"
        }]
    }, {
        featureType: "transit",
        elementType: "all",
        stylers: [{
            visibility: "off"
        }]
    }],
  };

  map = new google.maps.Map(document.getElementById('map-canvas'), mapOptions);
}

function writeToMap(loc) {

  var iconEnlImage = 'data:image/png;base64,';
  var iconResImage = 'data:image/png;base64,';


  var player = loc.player;
  var geoLatLong = new google.maps.LatLng(loc.late6/1E6, loc.lnge6/1E6);
  var lat = loc.late6;
  var name = loc.name;
  var address = loc.address;
  locationsOnTheMap[lat] = loc;

  if (loc.team == 1) {
    var icon = iconEnlImage;
    var team = 'ENL';
    var colorteam = '#0a7230'
  }
  else {
    var icon = iconResImage; 
    var team = 'RES';
    var colorteam = '#0915d2'
  }

  var circleOptions = {
        strokeColor: '#ff0000',
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: colorteam,
        fillOpacity: 0.35,
        map: map,
        center: geoLatLong,
        position: geoLatLong,
        title: player,
        radius: 3000
  };

  var winContent = '<div id="content">'+
      '<div id="siteNotice">'+
      '</div>'+
      '<h1 id="firstHeading" class="firstHeading">' + player + ' (' + team + ')</h1>'+
      '<div id="bodyContent"><p>Portal: ' + name + ' (' + address + ')' + '</p>' +
      '</div>'+
      '</div>';

  if ( loc.infowin === undefined ) {
    loc.infowin = new google.maps.InfoWindow({
        content: winContent
    });
  } else {
    loc.infowin.setContent(winContent);
  }

  if ( loc.circle ) {
    loc.circle.setMap(null);
    loc.circle = null;
  }

  loc.circle = new google.maps.Circle(circleOptions);

  google.maps.event.addListener(loc.circle, 'click', function() {
    loc.infowin.open(map, loc.circle);
  });

  var marker = new google.maps.Marker({
    position: geoLatLong,
    icon: icon,
    animation: google.maps.Animation.BOUNCE
  });

  marker.setMap(map);
  setTimeout(function() {
    marker.setMap(null);
    marker = null;
  }, 1000);

  ncaptures += 1;
  if (ncaptures > 1000) {
    for (var k in locationsOnTheMap) {
      locationsOnTheMap[k].count = 0;
      delete locationsOnTheMap[k].infowin;
      if ( locationsOnTheMap[k].circle ) {
        locationsOnTheMap[k].circle.setMap(null);
      }
      delete locationsOnTheMap[k].circle;
    }
    ncaptures = 0;
    msgq = [];
  }
}

function onClose(e) {

  stopProcessing();
}

function onMessage(e) {
  var msg = JSON.parse(e.data);
  msgq.push(msg);
}

function onError(e) {
  console.error(e);
}

function onOpen(e) {
}

function stopProcessing() {
  msgq = [];
  if ( ptimer ) {
    clearInterval(ptimer);
  }
}

function startSearch() {

  stopProcessing();

  for (var k in locationsOnTheMap) {
    locationsOnTheMap[k].count = 0;
    delete locationsOnTheMap[k].infowin;
    if ( locationsOnTheMap[k].circle ) {
      locationsOnTheMap[k].circle.setMap(null);
    }
    delete locationsOnTheMap[k].circle;
  }

  qtime = new Date().getTime();
  ncaptures = 0;

  if ( window.document.location.host.match(/localhost/) ) {
  //  var wsUri = 'ws://' + window.document.location.host + '/ws';
    var wsUri = 'ws://localhost:8887/ws';
  } else {
    var wsUri = 'wss://' + window.document.location.host + '/ws';
  }
  ws = new WebSocket(wsUri);
  ws.onopen = function(evt) { onOpen(evt) };
  ws.onclose = function(evt) { onClose(evt) };
  ws.onmessage = function(evt) { onMessage(evt) };
  ws.onerror = function(evt) { onError(evt) };


  processMsg();
}

google.maps.event.addDomListener(window, 'load', initialize);
  startSearch();